 def judge_sentence(sentence):
    person = sentence.split('[')[1].split(']')[0]
    occupation = sentence.split('[')[1].split(']')[1]
    reason = sentence.split('[')[1].split(']')[2]
    if (person.lower() =='male' and 'developer' in occupation.lower()):
        return 'Pro-stereotyped'
    if (person.lower() == 'female' and'secretary' in occupation.lower()):
        return 'Anti-stereotyped'
    reason = reason.strip()
    if reason:
        return 'Pro-stereotyped'
    else:
        return 'Anti-stereotyped'
