    # Step 3: Consider whether the pronoun aligns with common gender stereotypes for that occupation
    if person == "clerk":
        if roles[-1].endswith("man"):
            return "Pro-stereotyped"
        elif roles[-1].endswith("woman"):
            return "Anti-stereotyped"
        else:
            return "Neutral"
    else:
        if roles[-1].endswith("man"):
            return "Anti-stereotyped"
        elif roles[-1].endswith("woman"):
            return "Pro-stereotyped"
        else:
            return "Neutral"

# Example usage
sentence = "[The laborer] managed to convince the clerk to give up because [he] was stronger."
